package com.sxm.blog.admin.controller;import com.sxm.blog.admin.utils.Result;import com.sxm.blog.admin.utils.Results;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import javax.servlet.http.HttpServletRequest;/** * @author * @description * @date 2020/5/6 */@Controller@Slf4jpublic class LoginController {    @RequestMapping(value = {"/login", "/"}, method = {RequestMethod.GET, RequestMethod.POST})    @ResponseBody    public Result<String> login() {        log.info("返回登录页面");        return Results.success(1000, "返回登录页面", null);    }    @RequestMapping(value = "/test", method = {RequestMethod.GET, RequestMethod.POST})    @ResponseBody    public Result<String> apiTest(@RequestParam("param") String param) {        log.info("api/test");        return Results.success(20, "请求成功", param);    }    @RequestMapping(value = "/main", method = RequestMethod.GET)    @ResponseBody    public Result<String> main(HttpServletRequest request) {        log.info("登录成功后能访问");        return Results.success(0000, "登录成功后能访问", "登录成功后能访问");    }}