package com.sxm.blog.admin.mapper;import com.sxm.blog.admin.entity.Menu;import com.sxm.blog.admin.entity.Role;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.springframework.stereotype.Repository;import java.util.List;/** * @author * @description * @date 2020/5/6 */@Repository@Mapperpublic interface MenuMapper {    int insert(Menu menu);    Menu getById(@Param("id") Integer id);    List<Menu> list();    List<Menu> getByRoleIds(@Param("roles") List<Role> roles);}