package com.sxm.blog.admin.mapper;import com.sxm.blog.admin.entity.User;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.springframework.stereotype.Repository;import java.util.List;@Repository@Mapperpublic interface UserMapper {    int insert(User user);    User getById(@Param("id") Integer id);    List<User> list();    User getByUserName(@Param("username") String username);}