package com.sxm.blog.admin.security.handler;import com.sxm.blog.admin.security.CustomAccessDeniedHandler;import com.sxm.blog.admin.utils.Results;import lombok.extern.slf4j.Slf4j;import org.springframework.security.core.Authentication;import org.springframework.security.web.authentication.logout.SimpleUrlLogoutSuccessHandler;import org.springframework.stereotype.Component;import javax.servlet.ServletException;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * @author * @description * @date 2020/5/8 */@Slf4j@Componentpublic class LogoutSuccessHandler extends SimpleUrlLogoutSuccessHandler {    @Override    public void onLogoutSuccess(HttpServletRequest request, HttpServletResponse response, Authentication authentication) throws IOException, ServletException {        log.info("Logout successfully");        CustomAccessDeniedHandler.ControllerTools.print(response, Results.success(0, "退出成功", "退出成功").toJson());    }}