package com.sxm.blog.admin.utils;import lombok.Data;/** * @author * @description * @date 2020/5/6 */@Datapublic class Result<T> {    private boolean success;    private int code;    private String msg;    private T data;    public Result() {    }    public Result(boolean success, int code, String msg, T data) {        this.success = success;        this.code = code;        this.msg = msg;        this.data = data;    }    public boolean isSuccess() {        return this.success;    }}