package com.sxm.blog.admin.utils;import com.alibaba.fastjson.JSONObject;import lombok.Data;import java.io.Serializable;/** * @author * @description * @date 2020/5/6 */@Datapublic class Result<T> implements Serializable {    private static final long serialVersionUID = 6760155653374522419L;    private boolean success;    private int code;    private String msg;    private T data;    public Result() {    }    public Result(boolean success, int code, String msg, T data) {        this.success = success;        this.code = code;        this.msg = msg;        this.data = data;    }    public boolean isSuccess() {        return this.success;    }    public String toJson() {        return JSONObject.toJSONString(this);    }}