package com.sxm.blog.admin.utils;import com.sxm.blog.admin.entity.User;import org.springframework.security.core.context.SecurityContextHolder;/** * @author * @description * @date 2020/5/9 */public class UserUtil {    public static User getUser() {        User user = null;        Object principal = SecurityContextHolder.getContext().getAuthentication().getPrincipal();        if (principal instanceof User) {            user = (User) principal;        }        return user;    }}